// Show feedback animation
const doc = wwLib.getFrontDocument();
const container = doc.getElementById('auto_update_container');

if (container) {
  // Create a temporary overlay for visual feedback
  const overlay = doc.createElement('div');
  overlay.style.position = 'absolute';
  overlay.style.top = '0';
  overlay.style.left = '0';
  overlay.style.width = '100%';
  overlay.style.height = '100%';
  overlay.style.borderRadius = '12px';
  overlay.style.backgroundColor = event.value ? 'rgba(22, 163, 74, 0.2)' : 'rgba(220, 38, 38, 0.2)';
  overlay.style.opacity = '0';
  overlay.style.transition = 'opacity 0.3s ease';
  overlay.style.pointerEvents = 'none';

  container.style.position = 'relative';
  container.appendChild(overlay);

  // Show and then hide the overlay
  setTimeout(() => {
    overlay.style.opacity = '1';

    setTimeout(() => {
      overlay.style.opacity = '0';

      setTimeout(() => {
        container.removeChild(overlay);
      }, 300);
    }, 500);
  }, 10);
}

