// Send the trading mode selection to the API
const apiUrl = variables['7325544a-fd69-4b77-af8a-6c4baec79ac9'] + '/api/services/input_select/select_option';
const headers = [
  {
    key: variables['47773b1f-eb03-4d33-8c06-703a5b37568f'],
    value: variables['db4c3d5f-67d7-48ee-8125-795232a317ea']
  }
];

const data = {
  "entity_id": "input_select.ems_system_modes",
  "option": "Trading"
};

try {
  const response = await fetch(apiUrl, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      [headers[0].key]: headers[0].value
    },
    body: JSON.stringify(data)
  });

  if (response.ok) {
    // Close the popup
    utilsFunctions.closePopup(context);

    // Show success notification
    utilsFunctions.openPopup('f9713aab-9648-422a-a0e5-4749a8232bd1', {
      '7b42c672-9673-4f42-a7d7-eff53589a541': 'System mode set to Trading',
      'fd6aaf80-51dc-495f-9d8f-a7329a59f601': 'Success'
    });
  } else {
    throw new Error('API request failed');
  }
} catch (error) {
  // Close the popup
  utilsFunctions.closePopup(context);

  // Show error notification
